import React from "react";
import './TodoApp.css'
import ViewItems from './ViewItems'
export default class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      task: "",
      duedate: "",
      items:[]
    };
  }
  onInputChange = (e) => {
      console.log()
    this.setState({
        [e.target.name]:e.target.value
    })
  }
  addItem = () => {
    let items = this.state.items
    items.push({
        task:this.state.task,
        duedate:this.state.duedate
    })
    this.setState({
        task:'',
        duedate:'',
        items
    })
  }
  render() {
    return (
      <div className="todoApp">
        <div className="inputDiv">
          <label>Add Task</label>
          <input type="text" name = 'task' value = {this.state.task} onChange = {this.onInputChange}/>
          <label>Due date</label>
          <input type="date" name = 'duedate' value = {this.state.duedate} onChange = {this.onInputChange}/>
          <button onClick = {this.addItem}>Add</button>
        </div>
        {this.state.items && <ViewItems
        items = {this.state.items}
        />}
      </div>
    );
  }
}
