import React from "react";
export default class ViewItems extends React.Component {
  state = {
    status: false,
  };
  findDueDate = () => {
    let currentDate = new Date();
    let givenDate = this.props.duedate;
    givenDate = new Date(givenDate);
    if (givenDate > currentDate) {
      this.setState({
        status: true,
      });
    } else {
      this.setState({
        status: false,
      });
    }
  };
  render() {
    let items = this.props.items;
    return (
      <div>
        {items.length > 0 && (
          <table>
            <thead>
              <tr>
                <th>Task</th>
                <th>Due date</th>
                <th>Status</th>
              </tr>
            </thead>
            {items.map((item, i) => {
              return (
                <tbody>
                  <tr key={item}>
                    <td>{item.task}</td>
                    <td>{item.duedate}</td>
                  </tr>
                </tbody>
              );
            })}
          </table>
        )}
      </div>
    );
  }
}
