import React from "react";
import './TodoApp.css'

export default class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      item: "",
      duedate: "",
    };
  }
  onInputChange = (e) => {
    this.setState({
        [e.target.name]:e.target.value
    })
  }
  render() {
    return (
      <div className="todoApp">
        <div className="inputDiv">
          <label>Enter your todo item</label>
          <input type="text" value = {this.state.item} onChange = {this.onInputChange}/>
          <label>Due date</label>
          <input type="date" value = {this.state.duedate} onChange = {this.onInputChange}/>
        </div>
      </div>
    );
  }
}
