import React from "react";
import './TodoApp.css'
import ViewItems from './ViewItems'
export default class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      task: "",
      duedate: "",
      status : true,
      items:[]
    };
  }
  onInputChange = (e) => {
      console.log()
    this.setState({
        [e.target.name]:e.target.value
    })
  }
  addItem = () => {
    let currentDate = new Date();
    let givenDate = this.state.duedate;
    let items = this.state.items
    givenDate = new Date(givenDate);
    if(givenDate>currentDate){
       this.setState({
           status:false
       })
    }
    else{
        this.setState({
            status:true
        })
    }
    items.push({
        task:this.state.task,
        duedate:this.state.duedate,
        status:this.state.status
    })
    this.setState({
        task:'',
        duedate:'',
        status:'',
        items
    },()=>console.log(this.state.status,'pop'))
    console.log(this.state.items,'lol')

  }
  render() {
    return (
      <div className="todoApp">
        <div className="inputDiv">
          <label>Add Task</label>
          <input type="text" name = 'task' value = {this.state.task} onChange = {this.onInputChange}/>
          <label>Due date</label>
          <input type="date" name = 'duedate' value = {this.state.duedate} onChange = {this.onInputChange}/>
          <button onClick = {this.addItem}>Add</button>
        </div>
        <ViewItems
        items = {this.state.items}
        duedate = {this.state.duedate}
        status = {this.state.status}
        />
      </div>
    );
  }
}
